mapper.select:
select * from smbms_user


        id = #{id}


        AND userCode = #{userCode}


        AND userName = #{userName}


        AND userPassword = #{userPassword}

mapper.update:
update smbms_user

    userName = #{userName},
    userPassword = #{userPassword}

where id = #{id}

mapper.delete:
delete from smbms_user


        id = #{id}


        AND userCode = #{userCode}


        AND userName = #{userName}


        AND userPassword = #{userPassword}

mapper.select:
select count(1) as count from smbms_user u,smbms_role r where u.userRole = r.id
 and u.userName like CONCAT('%',#{userName,jdbcType=VARCHAR},'%') 
 and u.userRole = #{userCode}

select * from smbms_user

     and userName like CONCAT('%',#{userName},'%') 
     and userRole = #{userCode} 

order by creationDate DESC limit #{index},#{number}
